entry.programlisting:
context interne-benutzer {

  hint(SIP/21) 21 => {
    Dial(SIP/${EXTEN},40);
    VoiceMail(${EXTEN},u);
  }
  hint(SIP/22) 22 => {
    Dial(SIP/${EXTEN},40);
    VoiceMail(${EXTEN},u);
  }
}

context interne-benutzer {

  hint(SIP/21) 21 => {}
  hint(SIP/22) 22 => {}

  _2X => {
    Dial(SIP/${EXTEN},40);
    VoiceMail(${EXTEN},u);
  }
}

section.programlisting:
context interne-benutzer {

  // ...

  // Gespraechsuebernahme (PickUp)
  _*8X. => {
    Set(nst=${EXTEN:2});
    Verbose(1,${CALLERID(num)} will Anruf von ${nst} holen);
    // hier koennte eine Berechtigungspruefung eingebaut werden
    Pickup(${nst}@interne-benutzer);
  }
}

context interne-benutzer {

  // Verkaufsabteilung
  hint(SIP/21&SIP/22&SIP/23) 20 => {
    Dial(SIP/21&SIP/22&SIP/23,40);
  }

  // Gespraechsuebernahme (PickUp) von der Gruppe Verkauf
  _*820 => {
    Verbose(1,${CALLERID(num)} will Anruf von Gruppe Verkauf holen);
    Pickup(21@interne-benutzer&22@interne-benutzer&23@interne-benutzer);
  }
}

context interne-benutzer {

  // Verkaufsmitarbeiter
  _2X => {
    Set(__PICKUPMARK=verkauf);
    Dial(SIP/${EXTEN},40);
  }
  hint(SIP/21) 21 => {}
  hint(SIP/22) 22 => {}
  hint(SIP/23) 23 => {}

  // Verkaufsabteilung
  hint(SIP/21&SIP/22&SIP/23) 20 => {
    Set(__PICKUPMARK=verkauf);
    Dial(SIP/21&SIP/22&SIP/23,40);
  }

  // Gespraechsuebernahme von der Gruppe Verkauf
  _*820 => {
    Verbose(1,${CALLERID(num)} will Anruf von Gruppe Verkauf holen);
    Pickup(verkauf@PICKUPMARK);
  }
}

context interne-benutzer {

  // Verkaufsmitarbeiter
  _2X => {
    Set(__PICKUPMARK=200);
    // welcher Mitarbeiter zu welcher Pickup-Gruppe gehoert liesse sich
    // z.B. ueber ein ausgefeiltes AGI-Skript setzen
    Dial(SIP/${EXTEN}&Local/**${PICKUPMARK},40);
  }
  hint(SIP/21) 21 => {}
  hint(SIP/22) 22 => {}
  hint(SIP/23) 23 => {}

  // Pickup-Gruppen
  _**X. => {
    Verbose(1,Es klingelt in Pickup-Gruppe ${EXTEN:2});
  }
  hint(SIP/21&SIP/22&SIP/23) **200 => {}

  // Gespraechsuebernahme direkt
  _*8X. => {
    Set(nst=${EXTEN:2});
    Verbose(1,${CALLERID(num)} will Anruf von ${nst} holen);
    // hier koennte eine Berechtigungspruefung eingebaut werden
    Pickup(${nst}@interne-benutzer);
  }

  // Gespraechsuebernahme von einer Gruppe
  _*8**X. => {
    Set(pmark=${EXTEN:4});
    Verbose(1,${CALLERID(num)} will Anruf von Pickup-Gruppe ${pmark} holen);
    // hier koennte eine Berechtigungspruefung eingebaut werden
    Pickup(${pmark}@PICKUPMARK);
  }
}
