section.programlisting:
123 => {
    // ...
    if ("${input}" = "") {
        RaiseException(NOINPUT);
    }
    // ...
}

e => {
    Verbose(1,### An exception occurred);
    if ("${EXCEPTION(reason)}" = "NOINPUT") {
        Playback(nothing-was-entered);
    }
    else if ("${EXCEPTION(reason)}" = "RESPONSETIMEOUT") {
        Playback(you-waited-too-long);
    }
    else {
        Playback(an-error-occurred);
    }
    Hangup();
}
